import "./App.css";
import { useState } from "react";

function App() {
  const [result, setResult] = useState("");
  const [value, setValue] = useState("");
  const ranNum1 = Math.round(Math.random() * 9);
  const ranNum2 = Math.round(Math.random() * 9);

  function onSubmitForm(e) {
    e.preventDefault();
    const inputText = e.target[0].value;
    if (inputText === ranNum1 * ranNum2) {
      setResult("정답");
      setValue("");
    } else {
      setResult("땡!");
      setValue("");
    }
  }

  return (
    <div className="App">
      <div>
        {ranNum1} 곱하기 {ranNum2} ?
      </div>
      <form onSubmit={onSubmitForm}>
        <input type="number"></input>
        <input type="button" value="입력"></input>
      </form>
      <div>{result}</div>
    </div>
  );
}

export default App;
